mapper.select:
select
id, specialtitle, fmimglist, imgtag, titledescribe, oldprice, nowprice, ordertext, hddescribe, hdimglist,
tasktext, taskdescribe, taskimglist, specialtags, gototag, likesize, topnum, hometopnum, shopid, cityid,
sortnum, ssid, settime, createtime, state
from jhh_base_homespecial


        and id = #{id}


        and specialtitle = #{specialtitle}


        and fmimglist = #{fmimglist}


        and imgtag = #{imgtag}


        and titledescribe = #{titledescribe}


        and oldprice = #{oldprice}


        and nowprice = #{nowprice}


        and ordertext = #{ordertext}


        and hddescribe = #{hddescribe}


        and hdimglist = #{hdimglist}


        and tasktext = #{tasktext}


        and taskdescribe = #{taskdescribe}


        and taskimglist = #{taskimglist}


        and specialtags = #{specialtags}


        and gototag = #{gototag}


        and likesize = #{likesize}


        and topnum = #{topnum}


        and hometopnum = #{hometopnum}


        and shopid = #{shopid}


        and cityid = #{cityid}


        and sortnum = #{sortnum}


        and ssid = #{ssid}


        and settime = #{settime}


        and createtime = #{createtime}


        and state = #{state}
